// index.js
let canvas = document.getElementById('canvas');
let ctx = canvas.getContext('2d');

class EtchASketch {
  constructor() {
    this.currentX = canvas.width / 2;
    this.currentY = canvas.height / 2;
    this.direction = 'right';
    this.color = getRandomColor();
  }

  draw() {
    ctx.beginPath();
    ctx.arc(this.currentX, this.currentY, 20, 0, Math.PI * 2);
    ctx.fillStyle = this.color;
    ctx.fill();
  }

  updateDirection(event) {
    if (event.key === 'ArrowLeft') {
      this.direction = 'left';
    } else if (event.key === 'ArrowRight') {
      this.direction = 'right';
    } else if (event.key === 'ArrowUp') {
      this.direction = 'up';
    } else if (event.key === 'ArrowDown') {
      this.direction = 'down';
    }
  }

  updatePosition() {
    if (this.direction === 'left') {
      this.currentX -= 10;
    } else if (this.direction === 'right') {
      this.currentX += 10;
    } else if (this.direction === 'up') {
      this.currentY -= 10;
    } else if (this.direction === 'down') {
      this.currentY += 10;
    }
  }

  shake() {
    let x = Math.floor(Math.random() * canvas.width);
    let y = Math.floor(Math.random() * canvas.height);
    this.currentX = x;
    this.currentY = y;
  }
}

function getRandomColor() {
  let r = Math.floor(Math.random() * 256);
  let g = Math.floor(Math.random() * 256);
  let b = Math.floor(Math.random() * 256);
  return `rgb(${r}, ${g}, ${b})`;
}

let etchASketch = new EtchASketch();
document.addEventListener('keydown', etchASketch.updateDirection.bind(etchASketch));
setInterval(() => {
  etchASketch.draw();
  etchASketch.updatePosition();
}, 16);
```

